'use strict';

var bool;
var deepHasOwn = require( './../lib' );
var dHasOwn = deepHasOwn.factory('a_b_c', {
	'sep': '_'
});
var obj1 = {
	'a': {
		'b': {
			'c': 'd'
		}
	}
};

bool = deepHasOwn( obj1, 'a.b.c' );
console.log( bool );
// returns true

bool = deepHasOwn( obj1, [ 'a', 'b', 'c' ] );
console.log( bool );
// returns true

bool = deepHasOwn( obj1, 'a/b/c', {
	'sep': '/'
});
console.log( bool );
// returns true

bool = dHasOwn( obj1 );
console.log( bool );
// returns true

bool = deepHasOwn( obj1, 'a.b.c.d.e' );
console.log( bool );
// returns false

bool = deepHasOwn( obj1, [ 'a', 'b', 'c', 'd', 'e' ] );
console.log( bool );
// returns false

bool = deepHasOwn( obj1, 'a/b/c/d/e', {
	'sep': '/'
});
console.log( bool );
// returns false
