'use strict';

// MODULES //

var isObjectLike = require( '@stdlib/assert/is-object-like' );
var hasOwnProp = require( '@stdlib/assert/has-own-property' );


// MAIN //

/**
* Returns a boolean.
*
* @private
* @param {ObjectLike} objLikeVal - input object
* @param {Array} properties - list of properties defining a key path
* @returns {*} boolean
*/
function deepHasOwn( objLikeVal, properties ) {
	var propLength = properties.length;
	var value = objLikeVal;
	var hasOwn;
	var propInd;
	for ( propInd = 0; propInd < propLength; propInd++ ) {
		if (
			isObjectLike( value ) &&
			hasOwnProp( value, properties[ propInd ] )
		) {
			value = value[ properties[ propInd ] ];
			hasOwn = true;
		} else {
			hasOwn = false;
			return;
		}
	}
	return hasOwn;
} // end FUNCTION deepHasOwn()


// EXPORTS //

module.exports = deepHasOwn;
