'use strict';

/**
* Check whether an object contains a nested key path.
*
* @module @stdlib/assert/deep-has-own-property
*
* @example
* var deepHasOwn = require( '@stdlib/assert/deep-has-own-property' );
*
* var obj = { 'a': { 'b': { 'c': 'd' } } };
*
* var bool = deepHasOwn( obj, 'a.b.c' );
* // returns true
*
* var dHasOwn = factory( 'a/b/c', {
*     'sep': '/'
* });
*
* obj = { 'a': { 'b': { 'c': 'd' } } };
*
* bool = dHasOwn( obj );
* // returns true
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var deepHasOwn = require( './deep_has_own_property.js' );
var factory = require( './factory.js' );


// MAIN //

setReadOnly( deepHasOwn, 'factory', factory );


// EXPORTS //

module.exports = deepHasOwn;
