/* eslint-disable object-curly-newline */
'use strict';

// MODULES //

var tape = require( 'tape' );
var factory = require( './../lib/factory.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof factory, 'function', 'export is a function' );
	t.end();
});

tape( 'the function throws an error if provided a key path argument which is neither a string primitive or a key array', function test( t ) {
	var values;
	var i;

	values = [
		null,
		void 0,
		NaN,
		3,
		{},
		true,
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[ i ] ), TypeError, 'throws error when provided ' + ( typeof values[ i ] ) + ' as a path' );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			factory( value );
		};
	}
});

tape( 'the function throws an error if provided an options argument which is not an object', function test( t ) {
	var values;
	var i;

	values = [
		null,
		void 0,
		NaN,
		3,
		'3',
		[],
		true,
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[ i ] ), TypeError, 'throws error when provided ' + ( typeof values[ i ] ) + ' as an option' );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			factory( 'a.b', value );
		};
	}
});

tape( 'the function throws an error if provided a `sep` option argument which is not a string primitive', function test( t ) {
	var values;
	var i;

	values = [
		null,
		void 0,
		NaN,
		3,
		{},
		true,
		function noop() {}
	];
	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[ i ] ), TypeError, 'throws error when provided ' + ( typeof values[ i ] ) + ' as a sep option' );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			factory( 'a.b', {
				'sep': value
			});
		};
	}
});

tape( 'the function returns a function if provided with a valid option argument', function test( t ) {
	var has;

	has = factory( 'a/b/c', {
		'sep': '/'
	});

	t.strictEqual( typeof has, 'function', 'returns a function' );
	t.end();
});

tape( 'the function returns `false` if provided a `null` or `non-object`', function test( t ) {
	var values;
	var has;
	var i;

	values = [
		null,
		void 0,
		NaN,
		3,
		'3',
		true,
		function noop() {}
	];

	has = factory( 'a_b_c', {
		'sep': '_'
	});

	for ( i = 0; i < values.length; i++ ) {
		t.strictEqual( has( values[ i ] ), false, 'returns false when provided ' + ( typeof values[ i ] ) );
	}
	t.end();
});

tape( 'the created function returns `true` if an object has a specified path (defined as a delimited string)', function test( t ) {
	var bool;
	var has;

	has = factory( 'a.b.0.c' );

	bool = has( { 'a': { 'b': [ { 'c': 'd' } ] } } );
	t.strictEqual( bool, true, 'returns true' );
	t.end();
});

tape( 'the function returns `true` if an object has a specified path (defined as a key array)', function test( t ) {
	var bool;
	var has;

	has = factory( [ 'a', 'b', '0', 'c' ] );

	bool = has( { 'a': { 'b': [ { 'c': 'd' } ] } } );
	t.strictEqual( bool, true, 'returns true' );
	t.end();
});

tape( 'the function returns `true` if an object has a specified path (using non-default delimiter)', function test( t ) {
	var bool;
	var has;

	has = factory( 'a/b/0/c', {
		'sep': '/'
	});

	bool = has( { 'a': { 'b': [ { 'c': 'd' } ] } } );
	t.strictEqual( bool, true, 'returns true' );
	t.end();
});

tape( 'the function returns `false` if an object does not have a specified path (defined as a delimited string)', function test( t ) {
	var bool;
	var has;

	has = factory( 'a.b.c.0.d' );

	bool = has( { 'a': { 'b': [ { 'c': 'd' } ] } } );
	t.strictEqual( bool, false, 'returns false' );
	t.end();
});

tape( 'the function returns `false` if an object does not have a specified path (defined as a key array)', function test( t ) {
	var bool;
	var has;

	has = factory( [ 'a', 'b', 'c', '0', 'd' ] );

	bool = has( { 'a': { 'b': [ { 'c': 'd' } ] } } );
	t.strictEqual( bool, false, 'returns false' );
	t.end();
});

tape( 'the function returns `false` if an object does not have a specified path (using non-default delimiter)', function test( t ) {
	var bool;
	var has;

	has = factory( 'a/b/c/0/d', {
		'sep': '/'
	});

	bool = has( { 'a': { 'b': [ { 'c': 'd' } ] } } );
	t.strictEqual( bool, false, 'returns false' );
	t.end();
});
