'use strict';

// MODULES //

var tape = require( 'tape' );
var validate = require( './../lib/validate.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof validate, 'function', 'export is a function' );
	t.end();
});

tape( 'the function returns an error if provided an options argument which is not an object', function test( t ) {
	var values;
	var i;

	values = [
		null,
		void 0,
		NaN,
		3,
		'3',
		[],
		true,
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.strictEqual( validate( {}, values[ i ] ), true, 'returns a TypeError when provided ' + ( typeof values[ i ] ) + ' as an option' );
	}
	t.end();
});

tape( 'the function returns an error if provided a `sep` option argument which is not a string primitive', function test( t ) {
	var values;
	var err;
	var i;

	values = [
		null,
		void 0,
		NaN,
		3,
		new String( '3' ), // eslint-disable-line no-new-wrappers
		[],
		{},
		true,
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		err = validate( {}, {
			'sep': values[ i ]
		});
		t.strictEqual( err instanceof TypeError, true, 'returns a TypeError when provided ' + ( typeof values[ i ] ) + ' as a sep option' );
	}
	t.end();
});

tape( 'the function returns `null` if all options are of valid type (incorrect options properties)', function test( t ) {
	var opts;
	var obj;

	opts = {
		'foo': true,
		'bar': 'baz'
	};
	obj = {};

	t.strictEqual( validate( obj, opts ), null, 'returns null' );
	t.deepEqual( obj, {}, 'does not set any properties' );

	t.end();
});

tape( 'the function returns `null` if all options are of valid type (sep options property)', function test( t ) {
	var opts;
	var obj;

	opts = {
		'sep': '_'
	};
	obj = {};

	t.strictEqual( validate( obj, opts ), null, 'returns null' );
	t.strictEqual( obj.sep, '_', 'sets sep option as `_`' );

	opts = {
		'sep': '/'
	};
	obj = {};

	t.strictEqual( validate( obj, opts ), null, 'returns null' );
	t.strictEqual( obj.sep, '/', 'sets sep option as `/`' );

	t.end();
});

